// Colors
$primary: #18896e;
$secondary: #0c5c6e;

// Backgrounds
$alpha: 0.77;

// Texts
$heading-color: #000;
$text-color: lighten($heading-color, 20%);
$primary-text-color: #fff;

$line-height: 1.2;
$default-text-size: 16px;

// Spacing and orders
$units: 8px;
$border-radius: 4px;

// Animations
$transition-default: all 120ms linear;

// Responsive
$breakpoints: (
  xs: 576px,
  sm: 768px,
  md: 992px,
  lg: 1200px
);

// Menu
$menu-size-desktop: 30vw;
$menu-size: 82vw;

